import React, { useState } from "react";
import MoleculeViewer from "./MoleculeViewer";
import ExplanationCard from "./ExplainationCard";

export default function App() {
  const [compoundName, setCompoundName] = useState("");
  const [smiles, setSmiles] = useState("");
  const [resolvedSmiles, setResolvedSmiles] = useState("");
  const [loadingSmiles, setLoadingSmiles] = useState(false);

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const endpointMeaning = {
    "NR-AR": "Androgen",
    "NR-AR-LBD": "AR-Binding",
    "NR-AhR": "Pollutants",
    "NR-Aromatase": "Hormones",
    "NR-ER": "Estrogen",
    "NR-ER-LBD": "ER-Binding",
    "NR-PPAR-gamma": "Metabolism",
    "SR-ARE": "Oxidative",
    "SR-ATAD5": "DNA-Repair",
    "SR-HSE": "Heat-Shock",
    "SR-MMP": "Mitochondria",
    "SR-p53": "DNA-Damage",
  };

  const thStyle = {
    textAlign: "left",
    padding: "10px",
    background: "#f2f2f2",
    borderBottom: "2px solid #ddd",
  };

  const tdStyle = {
    padding: "10px",
    borderBottom: "1px solid #eee",
  };

  // ðŸ”¥ Fetch SMILES from PubChem
  async function fetchSmilesFromName(name) {
    setLoadingSmiles(true);
    try {
      const url = `https://pubchem.ncbi.nlm.nih.gov/rest/pug/compound/name/${encodeURIComponent(
        name
      )}/property/IsomericSMILES/JSON`;

      const res = await fetch(url);
      const json = await res.json();

      if (json?.PropertyTable?.Properties?.length > 0) {
        const s = json.PropertyTable.Properties[0].IsomericSMILES;
        setResolvedSmiles(s);
        setSmiles(s);
      } else {
        alert("Compound not found in PubChem!");
      }
    } catch (e) {
      alert("Error fetching compound from PubChem");
    }

    setLoadingSmiles(false);
  }

  async function handlePredict() {
    const inputSmiles = resolvedSmiles || smiles;

    if (!inputSmiles.trim()) {
      return alert("Enter a compound name or SMILES!");
    }

    setLoading(true);
    setResult(null);

    try {
      const res = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ smiles: inputSmiles }),
      });

      const data = await res.json();
      setResult(data);
    } catch (err) {
      setResult({ error: "Backend not reachable" });
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      style={{
        fontFamily: "Inter, sans-serif",
        background: "#f5f7fb",
        minHeight: "100vh",
        padding: "40px 0",
        display: "flex",
        justifyContent: "center",
      }}
    >
      {/* MAIN CARD */}
      <div
        style={{
          width: "650px",
          background: "white",
          padding: "30px",
          borderRadius: "16px",
          boxShadow: "0 4px 18px rgba(0,0,0,0.1)",
        }}
      >
        <h1 style={{ fontSize: "1.8rem", marginBottom: "12px" }}>
          Toxicity Predictor
        </h1>

        {/* ðŸ§ª COMPOUND NAME INPUT */}
        <label style={{ fontWeight: "600" }}>Enter Compound Name:</label>
        <div style={{ display: "flex", gap: "10px", marginBottom: "15px" }}>
          <input
            value={compoundName}
            onChange={(e) => setCompoundName(e.target.value)}
            placeholder="Example: ethanol, benzene, aspirin"
            style={{
              flex: 1,
              padding: "12px",
              borderRadius: "10px",
              border: "1px solid #ddd",
            }}
          />
          <button
            onClick={() => fetchSmilesFromName(compoundName)}
            disabled={loadingSmiles}
            style={{
              padding: "12px",
              background: "#2563eb",
              border: "none",
              color: "white",
              borderRadius: "10px",
              width: "140px",
            }}
          >
            {loadingSmiles ? "Searchingâ€¦" : "Fetch SMILES"}
          </button>
        </div>

        {/* ðŸ§¬ SMILES INPUT */}
        <label style={{ fontWeight: "600" }}>Or Enter SMILES:</label>
        <input
          value={smiles}
          onChange={(e) => {
            setSmiles(e.target.value);
            setResolvedSmiles(""); // user overrides auto-smiles
          }}
          placeholder="Example: CCO"
          style={{
            width: "100%",
            padding: "12px",
            marginTop: "8px",
            marginBottom: "20px",
            borderRadius: "10px",
            border: "1px solid #ddd",
          }}
        />

        {/* 2D Viewer */}
        {smiles && <MoleculeViewer smiles={smiles} />}

        {/* Predict */}
        <button
          onClick={handlePredict}
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            background: loading ? "#888" : "#4f46e5",
            border: "none",
            color: "white",
            fontSize: "1rem",
            borderRadius: "10px",
            marginBottom: "20px",
          }}
        >
          {loading ? "Predictingâ€¦" : "Predict Toxicity"}
        </button>

        {/* Toxicity Results */}
        {result && !result.error && (
          <div style={{ marginTop: "30px" }}>
            <h2>Results</h2>

            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th style={thStyle}>Endpoint</th>
                  <th style={thStyle}>Meaning</th>
                  <th style={thStyle}>Risk</th>
                </tr>
              </thead>

              <tbody>
                {Object.entries(result.predictions).map(([endpoint, obj]) => (
                  <tr key={endpoint}>
                    <td style={tdStyle}>{endpoint}</td>
                    <td style={tdStyle}>
                      <i>{endpointMeaning[endpoint]}</i>
                    </td>
                    <td style={tdStyle}>
                      <span
                        style={{
                          padding: "6px 12px",
                          borderRadius: "8px",
                          color: "white",
                          background:
                            obj.risk_level === "Toxic"
                              ? "#d9534f"
                              : obj.risk_level === "Medium Risk"
                              ? "#f0ad4e"
                              : "#5cb85c",
                        }}
                      >
                        {obj.risk_level}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <ExplanationCard results={result} />
          </div>
        )}
      </div>

      {/* RIGHT SIDE â€” PROPERTIES */}
      {result?.properties && (
        <div
          style={{
            marginTop: "30px",
            marginLeft: "50px",
            padding: "20px",
            background: "white",
            borderRadius: "14px",
            boxShadow: "0 4px 16px rgba(0,0,0,0.1)",
            height: "700px",
          }}
        >
          <h2 style={{ marginBottom: "15px" }}>ðŸ§¬ Properties</h2>

          <div
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr",
              gap: "12px",
            }}
          >
            {Object.entries(result.properties).map(([key, value]) => (
              <div
                key={key}
                style={{
                  padding: "12px",
                  background: "#f8f9fc",
                  borderRadius: "10px",
                  border: "1px solid #e3e6ef",
                }}
              >
                <div
                  style={{
                    fontSize: "0.85rem",
                    fontWeight: 600,
                    color: "#555",
                    textAlign: "center",
                  }}
                >
                  {key}
                </div>
                <div
                  style={{
                    fontSize: "1.1rem",
                    marginTop: "4px",
                    color: "#222",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  {value}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
